mapper.insert:
	insert into tbl_board(board_no, 
						  title,
						  content,
						  writer
						  )
  values(board_seq.nextval, 
  	   #{title},
  	   #{content},
  	   #{writer}
)

mapper.update:
update tbl_board
set    title = nvl(#{title}, title)

	   ,content =  #{content}

where  board_no = #{boardNo}
